function selectIssue(sender) {
	var clicked = setActive(sender, '.issue');
	var index = -1;
	for (i = 0; i < resourceButtonLabels.length; i++) {
		if (resourceButtonLabels[i][0]===$(clicked).text().toLowerCase()) {
			index=i;
		}
	}

	for (i = 0; i < resourceButtons.length; i++) {
		if(resourceButtonLabels[index][2+i]) {
			$(resourceButtons[i]).removeClass('hidden');
			resourceButtons[i].text(resourceButtonLabels[index][2+i]);
		}
		else {
			$(resourceButtons[i]).addClass('hidden');
		}
	}
}

function selectResource(sender) {
	var clicked = jQuery(sender);
	$('.resource').each(function (key, value) {
		$(this).removeClass('active');
	});
	$(clicked).addClass('active');
	var blockStart=campusResources.indexOf($(clicked).text())-backpedalIndex;
	var blockEnd=campusResources.indexOf('<h2 dir="ltr" >', blockStart+backpedalIndex);
	var block=campusResources.substring(blockStart, blockEnd);
	$('#resource-longform-description').empty().append(block);
}

function selectSubwayStop(sender) {
	var clicked = setActive(sender, '.subway-stop');

	var imgPath = 'img/subway_stops/' + $(clicked).text().replace(' ', '_') +'.jpg';
	$('#subway-stop-map').attr('src', imgPath);
}

function setActive(element, classname) {
	var toActive = jQuery(element);
	$(classname).each(function (key, value) {
		$(this).removeClass('active');
	});
	$(toActive).addClass('active');
	return toActive;
}

function setVisible(id, classname) {
	$(classname).each(function (key, value) {
		$(this).addClass('hidden');
	});
	$(id).removeClass('hidden');
	return $(id);
}

function format(toFormat) {
	return toFormat.replace(' ', '_').toLowerCase();
}

